"use strict";var Accordion={titleHeight:0,title:$(".list-accordion .accordion-title"),init:function(){Accordion.titleHeight=Accordion.title.outerHeight(),Accordion.title.on("click",function(){Accordion.collapseAll(),$(this).parent().hasClass("open")||$(this).parent().animate({height:$(this).next().height()+Accordion.titleHeight},250,"easeOutCubic",function(){$(this).addClass("open")})})},collapseAll:function(){$(".accordion-item.open").animate({height:Accordion.titleHeight},250,"easeOutCubic",function(){$(this).removeClass("open")})}};